



















































IF EXISTS(SELECT 'vw_other_correlation_fields' FROM INFORMATION_SCHEMA.VIEWS) BEGIN DROP VIEW vw_other_correlation_fields END
CREATE VIEW vw_other_correlation_fields AS SELECT id, datasource_name, table_name, column_name, (SELECT top 1 column_name FROM personal_data_locations WHERE id_personal_data_type = (SELECT top 1 id FROM personal_data_types WHERE name like 'CPF'))first_factor, (SELECT top 1 column_name FROM personal_data_locations WHERE id_personal_data_type = (SELECT top 1 id FROM personal_data_types WHERE name like 'RG'))second_factor, (SELECT top 1 column_name FROM personal_data_locations WHERE id_personal_data_type = (SELECT top 1 id FROM personal_data_types WHERE name like 'NOME'))third_factor FROM personal_data_locations WHERE id_personal_data_type not in (SELECT id FROM personal_data_types WHERE name in  ('NOME', 'CPF', 'RG'))