INSERT INTO jsf_label (jsf_bean, label, jsf_code) VALUES ('LEARNING_DATA_REG', 'TF-IDF é um algoritmo para medição de termos com ocorrência frequente que podem ser relevantes a um tópico. É recomendado para a leitura de trechos de texto, porém pode desequilibrar um trecho de texto devido a preposições como "a", "e, "da", "de", etc.', 'gc-label-0069')
INSERT INTO jsf_label (jsf_bean, label, jsf_code) VALUES ('LEARNING_DATA_REG', 'USAR ESTEMIZAÇÃO', 'gc-label-0070')
INSERT INTO jsf_label (jsf_bean, label, jsf_code) VALUES ('LEARNING_DATA_REG', 'A Estemização é um método para reduzir palavras flexionadas ao seu tronco ou raiz. Ajuda na busca transformando palavras flexionadas em seus sinônimos simples. NÃO É RECOMENDADO PARA IDIOMAS COM GRANDE DERIVAÇÃO LINGUÍSTICA (Ex: Alemão, Português).', 'gc-label-0071')
INSERT INTO jsf_label (jsf_bean, label, jsf_code) VALUES ('LEARNING_DATA_REG', 'FREQUÊNCIA MÍNIMA DE TERMOS', 'gc-label-0072')
INSERT INTO jsf_label (jsf_bean, label, jsf_code) VALUES ('LEARNING_DATA_REG', 'Só aplicável se usando TF-IDF. Remove termos que apareçam em mais de x% de documentos. Ideal para remover preposições desnecessárias que podem diminuir a precisão de um algoritmo.', 'gc-label-0073')
INSERT INTO jsf_label (jsf_bean, label, jsf_code) VALUES ('LEARNING_DATA_REG', 'NGRAMs', 'gc-label-0074')
INSERT INTO jsf_label (jsf_bean, label, jsf_code) VALUES ('LEARNING_DATA_REG', 'O Tokenizador NGram separa uma palavra por pedaços referentes à quantidade de letras específicadas. Exemplo: um número de 3-3 (mínimo/máximo) com a palavra "abelha" faria uma separação dos tokens "abe", "bel", "elh", "lha" para serem validadas.', 'gc-label-0075')
