INSERT INTO jsf_label (jsf_bean, label, jsf_code) VALUES ('LEARNING_DATA_REG', 'Mantém a quantidade especificada de palavras para predições mais rápidas e precisas em predições futuras. É recomendado manter um número alto neste item, mas não tão alto que ocupe uma quantidade grande de espaço do modelo (caso ele exista).', 'gc-label-0079')
